import React, { useEffect, useState } from 'react'

const Shorter = () => {
  const [shortenedUrls, setShortenedUrls] = useState([])
  const shortenUrl = () => {
    fetch('https://api.shrtco.de/v2/', {
      body: 'https://nika.magaria.com',
    })
      .then((res) => res.json())
      .then((json) => {
        // setShortenedUrls(...shortenedUrls, json)
        console.log(json)
      })
  }

  return (
    <div>
      <img src='/images/bg-shorten-desktop.svg' />
      <div>
        <input type='text' placeholder='Shorten a link here... ' />
        <button onClick={shortenUrl}>Shorten It!</button>
      </div>
    </div>
  )
}

export default Shorter
