import React, { useState } from 'react'

const Shorter = () => {
  const [shortenedUrls, setShortenedUrls] = useState([])
  const shortenUrl = () => {
    fetch('https://api.shrtco.de/v2/shorten?url=youtube.com')
      .then((res) => res.json())
      .then((json) => {
        if (!json.result) return
        setShortenedUrls([...shortenedUrls, json.result.full_short_link])
        console.log(json.result.full_short_link)
      })
  }

  return (
    <div>
      <div>
        <img src='/images/bg-shorten-desktop.svg' />
        <div>
          <input type='text' placeholder='Shorten a link here... ' />
          <button onClick={shortenUrl}>Shorten It!</button>
          {shortenedUrls.map((url) => {
            return <div>{url}</div>
          })}
        </div>
      </div>
    </div>
  )
}

export default Shorter
