import React, { useState } from 'react'
import Styles from './shorten.module.css'

const Shorter = () => {
  const [shortenedUrls, setShortenedUrls] = useState([])
  const shortenUrl = () => {
    fetch('https://api-ssl.bitly.com/v4/shorten', {
      method: 'POST',
      headers: {
        Authorization: 'Bearer 12fc5e0617b077322c758acb253a190c3254e801',
      },
      body: {
        domain: 'bit.ly',
        long_url:
          'https://www.twitch.tv/directory/game/Grand%20Theft%20Auto%20V',
      },
    })
      .then((res) => res.json())
      .then((json) => {
        if (!json.result) return
        setShortenedUrls([...shortenedUrls, json.result.full_short_link])
        console.log(json.result.full_short_link)
      })
  }

  return (
    <div className={Styles.div}>
      <div className={Styles.responsive}>
        <img src='/images/bg-shorten-desktop.svg' className={Styles.img} />
        <div className={Styles['input-div']}>
          <input
            type='text'
            placeholder='Shorten a link here... '
            className={Styles.input}
          />
          <button onClick={shortenUrl} className={Styles.button}>
            Shorten It!
          </button>
        </div>
        <div className={Styles['link-container']}>
          <ul className={Styles.ul}>
            <li className={Styles.li}>
              {shortenedUrls.map((url) => {
                return (
                  <div className={Styles['li-div']}>
                    {url} <button className={Styles.btn}>Copy</button>
                  </div>
                )
              })}
            </li>
          </ul>
        </div>
      </div>
    </div>
  )
}

export default Shorter
